--
-- 表的结构 `sy_lawyer`
--

CREATE TABLE `sy_lawyer` (
  `id` int(11) UNSIGNED NOT NULL,
  `admin_id` int(11) UNSIGNED NOT NULL,
  `category_id` int(11) UNSIGNED NOT NULL,
  `name` varchar(30) NOT NULL,
  `profile` varchar(1000) NOT NULL,
  `realm` varchar(1000) NOT NULL,
  `phone` varchar(20) NOT NULL,
  `location` varchar(255) NOT NULL,
  `work` varchar(255) NOT NULL,
  `image_id` int(11) UNSIGNED NOT NULL,
  `status` tinyint(1) UNSIGNED NOT NULL DEFAULT '1',
  `app_id` int(11) UNSIGNED NOT NULL DEFAULT '0',
  `create_time` int(11) UNSIGNED NOT NULL DEFAULT '0',
  `update_time` int(11) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `sy_lawyer`
--

INSERT INTO `sy_lawyer` (`id`, `admin_id`, `category_id`, `name`, `profile`, `realm`, `phone`, `location`, `work`, `image_id`, `status`, `app_id`, `create_time`, `update_time`) VALUES
(1, 0, 1, '刘峰平', '刘峰平，男，大学法学学士，通过了国家司法考试，取得了国家法律职业资格证书和律师执业资格证、证券从业资格，江西朗秋律师事务所专职律师。法学理论功底扎实，具有强烈的社会责任感，多次积极投身于社会的法律援助事业；实务经验丰富，参与了许多大型复杂的群体性案件的诉讼；刘律师坚持以事实为依据，以法律为准绳，对于当事人委托的案件恪尽职守，兢兢业业，最大限度地维护当事人的合法权益。', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '188888888', '江西朗秋律师事务所', '首席律师', 1, 1, 10001, 1598316183, 1598449985),
(2, 0, 1, '余彬', '现为江西省朗秋律师事务所专职律师。余彬律师坚持以事实为根据，以法律为准绳，恪尽职守，兢兢业业，最大限度的维护当事人的合法权益。专注从事证券虚假陈述索赔诉讼事务，有着敏锐的市场嗅觉和丰富的索赔经验，赢得了众多投资者的信任。', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '18174006383', '江西朗秋律师事务所', '首席律师', 1, 1, 10001, 1598321183, 100000),
(3, 0, 2, '姚招良', '姚招良律师,现为江西朗秋律师事务所专职律师（执业证号：13601201410604487）。姚招良律师多年来专注于代理证券虚假陈述责任纠纷案件，其代理完结及正在诉讼的案件包括京天利、大智慧、安硕信息、大连控股、方正证券、方正科技、超华科技、亿晶光电、雅百特、祥源文化、尔康制药、界龙实业等系列投资者索赔案件。积累了丰富的办案经验，帮助众多投资者成功获赔，获得了投资者的肯定。', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '18174006383', '江西朗秋律师事务所', '首席律师', 1, 1, 10001, 1598320383, 0),
(9, 0, 3, '曹晓芬', '32岁，毕业于南昌大学，现系江西朗秋律师事务所专职律师，南昌市法律服务中心服务律师，南昌市政府信访律师。', '擅长办理婚姻家事、合同纠纷、财产纠纷、公司事务及刑事辩护业务。曾办理了大量婚姻家庭、民间借贷等民商事案件及刑事辩护案件，取得了当事人的认可。\n\n', '18174006383', '江西朗秋律师事务所', '首席律师', 7, 1, 10001, 1598320283, 1598510058),
(10, 0, 6, '徐彭胄聪', '2011年毕业于华东交通大学，获得法学、工商管理双学士学位，曾供职于央企建设集团公司、多家全国性大型（财产险、养老险、寿险）保险上市公司，现为江西朗秋律师事务所专职律师、弈道律师团队律师，南昌市法律服务中心值班律师，南昌市中级人民法院信访驻点律师。在校期间担任学生会主席，工作期间担任公司法务、数据分析、总经理秘书、保险理赔管理等多岗位，熟悉多个行业的运行状况，有一定的社会阅历和社会关系。现具有全国保险从业资格证书、法律职业资格证书、全国员工福利规划师证书，为多家企事业单位、社会团体提供常年法律顾问服务，系某大型全国性保险销售服务有限公司江西区域唯一战略合作伙伴律师。同时，为满足不同层次的法律需求，自执业以来还积极为有法律需求的个人提供专项常年法律顾问服务。', '', '', '江西朗秋律师事务所', '首席律师', 6, 1, 10001, 1598320283, 1598510017),
(11, 0, 3, '姚招良', '姚招良律师,现为江西朗秋律师事务所专职律师（执业证号：13601201410604487）。姚招良律师多年来专注于代理证券虚假陈述责任纠纷案件，其代理完结及正在诉讼的案件包括京天利、大智慧、安硕信息、大连控股、方正证券、方正科技、超华科技、亿晶光电、雅百特、祥源文化、尔康制药、界龙实业等系列投资者索赔案件。积累了丰富的办案经验，帮助众多投资者成功获赔，获得了投资者的肯定。', '用良知执业，用真诚服务，为投资者争取每一分应得的赔偿。', '18174006383', '江西朗秋律师事务所', '首席律师', 5, 1, 10001, 1598320283, 1598509880),
(12, 0, 4, '余彬', '余彬，男，法学学士，现为江西省朗秋律师事务所专职律师。余彬律师坚持以事实为根据，以法律为准绳，恪尽职守，兢兢业业，最大限度的维护当事人的合法权益。', '专注从事证券虚假陈述索赔诉讼事务，有着敏锐的市场嗅觉和丰富的索赔经验，赢得了众多投资者的信任。', '18712341234', '江西朗秋律师事务所', '首席律师', 4, 1, 10001, 1598320283, 1598507857),
(13, 3, 1, '刘峰平', '派出机关和派出机构有什么区别', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '18174006383', '江西朗秋律师事务所', '首席律师', 2, 1, 10001, 1598320283, 1598449813),
(14, 0, 8, '卢冬萍', '专职律师，南昌市律协刑委会委员', '', '', '', '', 8, 1, 10001, 1598510156, 1598510679),
(15, 0, 1, '刘峰平', '刘峰平，男，大学法学学士，通过了国家司法考试，取得了国家法律职业资格证书和律师执业资格证、证券从业资格，江西朗秋律师事务所专职律师。法学理论功底扎实，具有强烈的社会责任感，多次积极投身于社会的法律援助事业；实务经验丰富，参与了许多大型复杂的群体性案件的诉讼；刘律师坚持以事实为依据，以法律为准绳，对于当事人委托的案件恪尽职守，兢兢业业，最大限度地维护当事人的合法权益。', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '188888888', '江西朗秋律师事务所', '首席律师', 1, 1, 10001, 1598316183, 1598449985),
(16, 0, 1, '余彬', '现为江西省朗秋律师事务所专职律师。余彬律师坚持以事实为根据，以法律为准绳，恪尽职守，兢兢业业，最大限度的维护当事人的合法权益。专注从事证券虚假陈述索赔诉讼事务，有着敏锐的市场嗅觉和丰富的索赔经验，赢得了众多投资者的信任。', '合同纠纷，劳动人事争议， 婚姻家庭，人身损害赔偿（交通、医疗、工伤），刑事辩护及各类非诉业务（公司法务，起草、审查合同，出具律师见证书，律师函，法律意见书等）。', '18174006383', '江西朗秋律师事务所', '首席律师', 1, 1, 10001, 1598321183, 100000),
