CREATE TABLE `sy_lawyer_cat` (
  `category_id` int(11) UNSIGNED NOT NULL COMMENT '产品分类id',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '分类名称',
  `sort` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '排序方式(数字越小越靠前)',
  `app_id` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '应用id',
  `create_time` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '更新时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='产品分类表';

