--
-- 表的结构 `sy_mediate`
--

CREATE TABLE `sy_mediate` (
  `id` int(11) UNSIGNED NOT NULL COMMENT 'id',
  `no` varchar(20) NOT NULL DEFAULT '' COMMENT '案件码',
  `uid` int(11) UNSIGNED NOT NULL,
  `cid` int(11) NOT NULL COMMENT '类型',
  `name` varchar(30) NOT NULL DEFAULT '' COMMENT '姓名',
  `mobile` varchar(20) NOT NULL DEFAULT '' COMMENT '联系方式',
  `idcard` varchar(20) NOT NULL DEFAULT '' COMMENT '身份证号',
  `my_area` varchar(255) NOT NULL,
  `my_address` varchar(255) NOT NULL,
  `appeal` text NOT NULL COMMENT '诉求',
  `other_name` varchar(30) NOT NULL DEFAULT '' COMMENT '对方姓名',
  `other_phone` varchar(20) NOT NULL DEFAULT '' COMMENT '对方联系方式',
  `other_area` varchar(255) NOT NULL,
  `other_address` varchar(255) NOT NULL,
  `text` text NOT NULL COMMENT '纠纷描述',
  `area` varchar(255) NOT NULL COMMENT '地区',
  `address` varchar(255) NOT NULL COMMENT '位置',
  `court_time` int(11) NOT NULL COMMENT '开庭时间',
  `court_address` varchar(255) NOT NULL COMMENT '开庭地址',
  `remark` varchar(1000) NOT NULL COMMENT '备注',
  `summary` varchar(1000) NOT NULL DEFAULT '' COMMENT '案情概述',
  `process` varchar(1000) NOT NULL DEFAULT '' COMMENT '调解过程',
  `agreement` varchar(1000) NOT NULL DEFAULT '' COMMENT '调解协议内容',
  `mediator` varchar(1000) NOT NULL DEFAULT '' COMMENT '承办调解员',
  `closing_time` int(11) UNSIGNED NOT NULL DEFAULT '0' COMMENT '结案时间',
  `closing_person` varchar(255) NOT NULL DEFAULT '' COMMENT '结案人',
  `times` tinyint(1) UNSIGNED NOT NULL DEFAULT '1' COMMENT '轮次',
  `way` tinyint(1) UNSIGNED NOT NULL DEFAULT '0' COMMENT '步骤',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `allow_edit` tinyint(1) NOT NULL DEFAULT '0' COMMENT '用户允许编辑数据',
  `app_id` int(11) UNSIGNED NOT NULL DEFAULT '0',
  `create_time` int(11) UNSIGNED NOT NULL DEFAULT '0',
  `update_time` int(11) UNSIGNED NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

